using System;namespace PalindromeExercise{    public class WordSmith    {        public bool IsPalindrome(string word)        {            if (string.IsNullOrEmpty(word))            {                return false;            }            string trimmedWord = word.Trim().ToLower();            int left = 0;            int right = word.Length - 1;            while(left < right)            {                if (trimmedWord[left] != trimmedWord[right])                {                    return false;                }                left++;                right--;            }            return true;        }    }}